function assert(a, b) {

   try {
      deepStrictEqual(a, b)
   }
   catch (e) {
      a = JSON.stringify(a, null, 2)
      a = a.slice(0, 500) + (a.length > 500 ? " ..." : "")

      b = JSON.stringify(b, null, 2)
      b = b.slice(0, 500) + (b.length > 500 ? " ..." : "")

      throw new Error(`\n\nEXPECTED\n${b}\n\nACTUAL\n${a}`)
   }

}


// Replace ArrayBuffers with their sha256 hashes.

function hashArrayBuffers(object) {

   if (typeof object !== "object" || object === null)
      return

   const keys = Object.keys(object)
   for (const key of keys) {
      if (object[key] instanceof ArrayBuffer)
         object[key] = sha256(object[key])
      else
         hashArrayBuffers(object[key])
   }

}


// Convert an ArrayBuffer of [r,g,b,a,r,g,b,a...] to [{r,g,b,a}...].
function rgbArray(buffer) {
   let colours = []
   let values = new Uint8Array(buffer)
   for (let i = 0; i < values.length; i += 4)
      colours.push({ r: values[i], g: values[i + 1], b: values[i + 2], a: values[i + 3] })
   return colours
}


function read(path) {

   return new Promise((resolve, reject) => {
      fs.readFile(path, (error, result) => {
         if (error)
            reject(error)
         resolve(result)
      })
   })

}

function exists(path) {

   return fs.existsSync(path)

}
